import React, { useState, useEffect } from 'react';
import {
  Box,
  Button,
  Container,
  FormControl,
  FormLabel,
  Input,
  Select,
  Stack,
  Textarea,
  useToast,
  VStack,
  HStack,
  IconButton,
  Text,
  Divider,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalBody,
  ModalCloseButton,
  ModalFooter,
  Grid,
  GridItem,
  Heading,
  Switch,
  FormHelperText,
  Badge,
  Card,
  CardBody,
  CardHeader,
  CardFooter,
  Flex,
  Spacer,
  Tooltip,
  useColorModeValue,
  Icon,
} from '@chakra-ui/react';
import { AddIcon, DeleteIcon, DragHandleIcon, ViewIcon, EditIcon, CopyIcon } from '@chakra-ui/icons';
import { useNavigate, useParams } from 'react-router-dom';
import { supabase } from '../utils/supabase';
import { Form, FormField } from '../types';
import { DragDropContext, Droppable, Draggable, DropResult, DroppableProvided, DraggableProvided } from '@hello-pangea/dnd';
import { FaHeading, FaRegKeyboard, FaHashtag, FaCalendarAlt, FaChevronDown, FaDotCircle, FaCheckSquare, FaFileUpload, FaWhatsapp, FaPlus } from 'react-icons/fa';

const FormBuilder: React.FC = () => {
  const { formId } = useParams<{ formId: string }>();
  const [form, setForm] = useState<Partial<Form>>({
    title: '',
    description: '',
    fields: [],
    status: 'draft',
  });
  const [isLoading, setIsLoading] = useState(false);
  const [isPreviewMode, setIsPreviewMode] = useState(false);
  const { isOpen, onOpen, onClose } = useDisclosure();
  const [draftTitle, setDraftTitle] = useState('');
  const toast = useToast();
  const navigate = useNavigate();

  // Color scheme from UI screenshot
  const green = '#22C55E';
  const blue = '#38BDF8';
  const bgMain = '#F8FAFC';
  const cardBg = '#F1F5F9';
  const borderColor = '#E5E7EB';
  const mutedText = '#64748B';
  const headingText = '#0F172A';

  // Elements panel state
  const [elementsTab, setElementsTab] = useState<'basic' | 'advanced' | 'layout'>('basic');
  const [search, setSearch] = useState('');

  // List of available elements with appropriate icon components
  const availableElements = [
    { type: 'text', label: 'Heading', icon: FaHeading },
    { type: 'text', label: 'Text Input', icon: FaRegKeyboard },
    { type: 'number', label: 'Number', icon: FaHashtag },
    { type: 'date', label: 'Date Picker', icon: FaCalendarAlt },
    { type: 'select', label: 'Dropdown', icon: FaChevronDown },
    { type: 'radio', label: 'Radio Buttons', icon: FaDotCircle },
    { type: 'checkbox', label: 'Checkbox', icon: FaCheckSquare },
    { type: 'file', label: 'File Upload', icon: FaFileUpload },
  ];

  // Filtered elements for search
  const filteredElements = availableElements.filter(e =>
    e.label.toLowerCase().includes(search.toLowerCase())
  );

  // Drag and drop logic for cross-column
  const onDragEnd = (result: DropResult) => {
    const { source, destination, draggableId } = result;
    if (!destination) return;

    // Drag from Elements panel to Form Designer
    if (source.droppableId === 'elements' && destination.droppableId === 'fields') {
      const element = availableElements[source.index];
      const newField: FormField = {
        id: Date.now().toString(),
        type: element.type as FormField['type'],
        label: `New ${element.label}`,
        required: false,
      };
      setForm(prev => {
        const fields = prev.fields ? [...prev.fields] : [];
        fields.splice(destination.index, 0, newField);
        return { ...prev, fields };
      });
      return;
    }

    // Reorder within Form Designer
    if (source.droppableId === 'fields' && destination.droppableId === 'fields') {
      const items = Array.from(form.fields ?? []);
      const [reorderedItem] = items.splice(source.index, 1);
      items.splice(destination.index, 0, reorderedItem);
      setForm(prev => ({ ...prev, fields: items }));
      return;
    }
  };

  useEffect(() => {
    if (formId) {
      fetchForm();
    }
  }, [formId]);

  const fetchForm = async () => {
    try {
      const { data, error } = await supabase
        .from('forms')
        .select('*')
        .eq('id', formId)
        .single();

      if (error) throw error;
      setForm(data);
    } catch (error: any) {
      toast({
        title: 'Error',
        description: error.message || 'Failed to fetch form',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    }
  };

  const handleSave = async (isDraft: boolean = true) => {
    try {
      setIsLoading(true);
      const formData = {
        ...form,
        status: isDraft ? 'draft' : 'published',
      };

      if (formId) {
        const { error } = await supabase
          .from('forms')
          .update(formData)
          .eq('id', formId);

        if (error) throw error;
      } else {
        const { data, error } = await supabase
          .from('forms')
          .insert([formData])
          .select()
          .single();

        if (error) throw error;
        navigate(`/forms/${data.id}/edit`);
      }

      toast({
        title: 'Success',
        description: `Form ${isDraft ? 'saved as draft' : 'published'} successfully`,
        status: 'success',
        duration: 3000,
        isClosable: true,
      });

      if (!isDraft) {
        navigate('/dashboard');
      }
    } catch (error: any) {
      toast({
        title: 'Error',
        description: error.message || 'Failed to save form',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    } finally {
      setIsLoading(false);
    }
  };

  const addField = (type: FormField['type']) => {
    const newField: FormField = {
      id: Date.now().toString(),
      type,
      label: `New ${type} field`,
      required: false,
    };
    setForm(prev => ({
      ...prev,
      fields: [...(prev.fields ?? []), newField],
    }));
  };

  const updateField = (index: number, field: Partial<FormField>) => {
    setForm(prev => ({
      ...prev,
      fields: prev.fields?.map((f, i) => 
        i === index ? { ...f, ...field } : f
      ),
    }));
  };

  const removeField = (index: number) => {
    setForm(prev => ({
      ...prev,
      fields: prev.fields?.filter((_, i) => i !== index),
    }));
  };

  const renderFieldEditor = (field: FormField, index: number, provided: DraggableProvided) => (
    <Box
      ref={provided.innerRef}
      {...provided.draggableProps}
    >
      <Card 
        key={field.id} 
        mb={4} 
        variant="outline" 
        bg={cardBg}
        borderColor={borderColor}
        boxShadow="sm"
        _hover={{ boxShadow: 'md' }}
        transition="all 0.2s"
      >
        <CardHeader pb={0}>
          <Flex align="center">
            <Box {...provided.dragHandleProps} cursor="grab">
              <DragHandleIcon mr={2} color={mutedText} />
            </Box>
            <Heading size="sm" color={headingText}>Field {index + 1}</Heading>
            <Spacer />
            <Tooltip label="Delete field">
              <IconButton
                aria-label="Delete field"
                icon={<DeleteIcon />}
                size="sm"
                colorScheme="red"
                variant="ghost"
                onClick={() => removeField(index)}
              />
            </Tooltip>
          </Flex>
        </CardHeader>
        <CardBody>
          <VStack spacing={4}>
            <FormControl>
              <FormLabel fontWeight="medium" color={headingText}>Label</FormLabel>
              <Input
                value={field.label}
                onChange={(e) => updateField(index, { label: e.target.value })}
                placeholder="Enter field label"
                size="md"
                borderRadius="md"
                bg="white"
                borderColor={borderColor}
              />
            </FormControl>

            <FormControl>
              <FormLabel fontWeight="medium" color={headingText}>Type</FormLabel>
              <Select
                value={field.type}
                onChange={(e) => updateField(index, { type: e.target.value as FormField['type'] })}
                size="md"
              >
                <option value="text">Text</option>
                <option value="number">Number</option>
                <option value="email">Email</option>
                <option value="tel">Phone</option>
                <option value="textarea">Text Area</option>
                <option value="select">Select</option>
                <option value="radio">Radio</option>
                <option value="checkbox">Checkbox</option>
                <option value="date">Date</option>
              </Select>
            </FormControl>

            {(field.type === 'select' || field.type === 'radio' || field.type === 'checkbox') && (
              <FormControl>
                <FormLabel fontWeight="medium" color={headingText}>Options</FormLabel>
                <Input
                  value={field.options?.join(', ') || ''}
                  onChange={(e) => updateField(index, { 
                    options: e.target.value.split(',').map(opt => opt.trim()).filter(Boolean)
                  })}
                  placeholder="Enter options (comma-separated)"
                  size="md"
                  borderRadius="md"
                  bg="white"
                  borderColor={borderColor}
                />
              </FormControl>
            )}

            <FormControl>
              <FormLabel fontWeight="medium" color={headingText}>Placeholder</FormLabel>
              <Input
                value={field.placeholder || ''}
                onChange={(e) => updateField(index, { placeholder: e.target.value })}
                placeholder="Enter placeholder text"
                size="md"
                borderRadius="md"
                bg="white"
                borderColor={borderColor}
              />
            </FormControl>

            <FormControl display="flex" alignItems="center">
              <FormLabel mb="0" fontWeight="medium" color={headingText}>Required</FormLabel>
              <Switch
                isChecked={field.required}
                onChange={(e) => updateField(index, { required: e.target.checked })}
                colorScheme="blue"
              />
            </FormControl>
          </VStack>
        </CardBody>
      </Card>
    </Box>
  );

  const renderFieldPreview = (field: FormField) => (
    <Card 
      key={field.id} 
      mb={4} 
      variant="outline" 
      bg={cardBg}
      borderColor={borderColor}
      boxShadow="sm"
    >
      <CardBody>
        <VStack spacing={4} align="stretch">
          <HStack justify="space-between">
            <FormLabel fontWeight="medium" mb={0} color={headingText}>
              {field.label}
              {field.required && <Badge ml={2} colorScheme="red" fontSize="xs">Required</Badge>}
            </FormLabel>
          </HStack>
          {renderFieldInput(field)}
        </VStack>
      </CardBody>
    </Card>
  );

  const renderFieldInput = (field: FormField) => {
    switch (field.type) {
      case 'text':
      case 'email':
      case 'tel':
      case 'number':
        return (
          <Input
            type={field.type}
            placeholder={field.placeholder}
            size="md"
            borderRadius="md"
            bg="white"
            borderColor={borderColor}
          />
        );
      case 'textarea':
        return (
          <Input
            as="textarea"
            placeholder={field.placeholder}
            size="md"
            rows={3}
            borderRadius="md"
            bg="white"
            borderColor={borderColor}
          />
        );
      case 'select':
        return (
          <Select placeholder={field.placeholder} size="md" borderRadius="md" bg="white" borderColor={borderColor}>
            {field.options?.map((option, i) => (
              <option key={i} value={option}>{option}</option>
            ))}
          </Select>
        );
      case 'radio':
        return (
          <VStack align="start" spacing={2}>
            {field.options?.map((option, i) => (
              <HStack key={i}>
                <input type="radio" name={field.id} value={option} />
                <Text>{option}</Text>
              </HStack>
            ))}
          </VStack>
        );
      case 'checkbox':
        return (
          <VStack align="start" spacing={2}>
            {field.options?.map((option, i) => (
              <HStack key={i}>
                <input type="checkbox" value={option} />
                <Text>{option}</Text>
              </HStack>
            ))}
          </VStack>
        );
      case 'date':
        return (
          <Input type="date" size="md" borderRadius="md" bg="white" borderColor={borderColor} />
        );
      default:
        return null;
    }
  };

  return (
    <Box bg={bgMain} minH="100vh" py={8}>
      <Container maxW="container.xl">
        <VStack spacing={8} align="stretch">
          <Flex justify="space-between" align="center">
            <Heading size="lg" color={headingText} fontWeight="bold">
              {formId ? 'Edit Form' : 'Create Form'}
            </Heading>
            <HStack spacing={4}>
              <Button
                leftIcon={<Icon as={ViewIcon} boxSize={5} />}
                onClick={() => setIsPreviewMode(!isPreviewMode)}
                variant="outline"
                color={blue}
                borderColor={blue}
                borderWidth={2}
                _hover={{ bg: blue, color: 'white' }}
                fontWeight="semibold"
              >
                {isPreviewMode ? 'Edit Mode' : 'Preview Mode'}
              </Button>
              <Button
                bg={green}
                color="white"
                _hover={{ bg: '#16A34A' }}
                onClick={() => handleSave(false)}
                isLoading={isLoading}
                size="md"
                fontWeight="semibold"
                borderRadius="lg"
                px={6}
              >
                Publish
              </Button>
              <Button
                onClick={() => handleSave(true)}
                isLoading={isLoading}
                variant="outline"
                color={blue}
                borderColor={blue}
                borderWidth={2}
                _hover={{ bg: blue, color: 'white' }}
                size="md"
                fontWeight="semibold"
                borderRadius="lg"
                px={6}
              >
                Save as Draft
              </Button>
            </HStack>
          </Flex>

          <Box mb={8}>
            <Card bg={cardBg} borderRadius="2xl" border="1px solid" borderColor={borderColor} boxShadow="sm">
              <CardHeader>
                <Heading size="md" color={headingText} fontWeight="bold">Form Settings</Heading>
              </CardHeader>
              <CardBody>
                <Grid templateColumns={{ base: '1fr', md: '1fr 1fr' }} gap={6}>
                  <FormControl>
                    <FormLabel fontWeight="medium" color={headingText}>Form Title</FormLabel>
                    <Input
                      value={form.title}
                      onChange={(e) => setForm(prev => ({ ...prev, title: e.target.value }))}
                      placeholder="e.g., Customer Feedback Form"
                      size="lg"
                      borderRadius="md"
                      bg="white"
                      borderColor={borderColor}
                    />
                  </FormControl>
                  <FormControl>
                    <FormLabel fontWeight="medium" color={headingText}>Form Description (Optional)</FormLabel>
                    <Textarea
                      value={form.description}
                      onChange={(e) => setForm(prev => ({ ...prev, description: e.target.value }))}
                      placeholder="Brief description of your form"
                      borderRadius="md"
                      bg="white"
                      borderColor={borderColor}
                    />
                  </FormControl>
                  <FormControl>
                    <FormLabel fontWeight="medium" color={headingText}>WhatsApp Number</FormLabel>
                    <HStack>
                      <Select
                        value={form.countryCode || '+91'}
                        onChange={e => setForm(prev => ({ ...prev, countryCode: e.target.value }))}
                        borderRadius="md"
                        bg="white"
                        borderColor={borderColor}
                        w="40%"
                      >
                        <option value="+1">+1 (US/CA)</option>
                        <option value="+44">+44 (UK)</option>
                        <option value="+91">+91 (IN)</option>
                        <option value="+61">+61 (AU)</option>
                        <option value="+55">+55 (BR)</option>
                        <option value="+33">+33 (FR)</option>
                        <option value="+49">+49 (DE)</option>
                        <option value="+81">+81 (JP)</option>
                        <option value="+86">+86 (CN)</option>
                        <option value="+52">+52 (MX)</option>
                      </Select>
                      <Input
                        value={form.whatsappNumber || ''}
                        onChange={e => setForm(prev => ({ ...prev, whatsappNumber: e.target.value }))}
                        placeholder="Your WhatsApp Number"
                        borderRadius="md"
                        bg="white"
                        borderColor={borderColor}
                        w="60%"
                      />
                    </HStack>
                    <FormHelperText color={mutedText}>Form submissions will be sent to this WhatsApp number</FormHelperText>
                  </FormControl>
                  <FormControl>
                    <FormLabel fontWeight="medium" color={headingText}>Form Theme</FormLabel>
                    <Select
                      value={form.theme || 'default'}
                      onChange={e => setForm(prev => ({ ...prev, theme: e.target.value }))}
                      borderRadius="md"
                      bg="white"
                      borderColor={borderColor}
                    >
                      <option value="default">Default</option>
                      <option value="minimal">Minimal</option>
                      <option value="professional">Professional</option>
                      <option value="colorful">Colorful</option>
                    </Select>
                  </FormControl>
                </Grid>
              </CardBody>
            </Card>
          </Box>

          <Flex direction={{ base: 'column', lg: 'row' }} gap={6}>
            {/* Elements Panel */}
            <Box w={{ base: '100%', lg: '300px' }} bg={cardBg} borderRadius="2xl" border="1px solid" borderColor={borderColor} boxShadow="sm" mb={{ base: 6, lg: 0 }}>
              <Box borderBottom="1px solid" borderColor={borderColor} pb={3} pt={4} px={5} borderTopRadius="2xl">
                <Heading size="md" color={headingText} fontWeight="bold">Form Elements</Heading>
                <Text fontSize="sm" color={mutedText} mt={1}>Add elements to your form</Text>
              </Box>
              <Box px={4} py={4}>
                {filteredElements.map((el, idx) => (
                  <Box
                    as="button"
                    key={el.type + idx}
                    w="100%"
                    bg="white"
                    borderRadius="md"
                    boxShadow="sm"
                    border="1px solid"
                    borderColor={borderColor}
                    display="flex"
                    alignItems="center"
                    px={4}
                    py={3}
                    mb={2}
                    cursor="pointer"
                    _hover={{ bg: '#E5E7EB', color: green, boxShadow: 'md' }}
                    transition="all 0.15s"
                    onClick={() => {
                      const newField = {
                        id: Date.now().toString(),
                        type: el.type as FormField['type'],
                        label: `New ${el.label}`,
                        required: false,
                      };
                      setForm(prev => ({
                        ...prev,
                        fields: [...(prev.fields ?? []), newField],
                      }));
                    }}
                  >
                    <Icon as={el.icon as React.ElementType} boxSize={5} mr={3} />
                    <Text fontWeight="medium" color={headingText}>{el.label}</Text>
                  </Box>
                ))}
              </Box>
            </Box>

            {/* Form Builder Panel */}
            <Box flex="1" bg={cardBg} borderRadius="2xl" border="1px solid" borderColor={borderColor} boxShadow="sm">
              <Box borderBottom="1px solid" borderColor={borderColor} p={4} borderTopRadius="2xl" display="flex" justifyContent="space-between" alignItems="center">
                <Heading size="md" color={headingText} fontWeight="bold">Form Builder</Heading>
              </Box>
              <Box p={6}>
                {/* Form Header Preview */}
                <Box mb={8} textAlign="center">
                  <Heading size="lg" color={headingText} fontWeight="bold" mb={2} minH="32px">
                    {form.title || 'Form Title'}
                  </Heading>
                  <Text color={mutedText} minH="24px">
                    {form.description || 'Form description (optional)'}
                  </Text>
                </Box>
                {/* Form Fields Container */}
                <Box id="formFieldsContainer">
                  {((form.fields ?? [])).map((field, idx) => (
                    <Box key={field.id} p={4} border="1px solid" borderColor={borderColor} borderRadius="lg" bg="white" position="relative">
                      {/* Controls: Move up/down, edit, delete, required toggle */}
                      <Flex position="absolute" right={2} top={2} gap={1}>
                        <IconButton aria-label="Move up" icon={<Icon as={AddIcon} />} size="sm" variant="ghost" isDisabled={idx === 0} />
                        <IconButton
                          aria-label="Move down"
                          icon={<Icon as={AddIcon} transform="rotate(180deg)" />}
                          size="sm"
                          variant="ghost"
                          isDisabled={idx === ((form.fields?.length ?? 0) - 1)}
                        />
                        <IconButton aria-label="Edit" icon={<Icon as={EditIcon} />} size="sm" variant="ghost" />
                        <IconButton aria-label="Delete" icon={<Icon as={DeleteIcon} />} size="sm" variant="ghost" colorScheme="red" onClick={() => removeField(idx)} />
                      </Flex>
                      <Text fontWeight="medium" color={headingText} mb={2}>{field.label}</Text>
                      {/* Render field preview (simplified) */}
                      <Input placeholder={field.placeholder || 'Field input'} isDisabled />
                      {/* Required toggle */}
                      <Flex align="center" mt={2}>
                        <Text color={mutedText} fontSize="sm" mr={2}>Required</Text>
                        <Switch isChecked={field.required} onChange={() => updateField(idx, { required: !field.required })} colorScheme="green" />
                      </Flex>
                    </Box>
                  ))}
                  {/* Submit Button Preview */}
                  <Box mt={8} textAlign="center">
                    <Button bg={green} color="white" px={6} py={3} borderRadius="lg" fontSize="lg" fontWeight="medium" isDisabled>
                      Submit
                    </Button>
                    <Text mt={2} color={mutedText} fontSize="sm">
                      By submitting, you'll receive a confirmation on WhatsApp
                    </Text>
                  </Box>
                </Box>
              </Box>
            </Box>
          </Flex>
        </VStack>
      </Container>
    </Box>
  );
};

export default FormBuilder;
