import React, { useState, useEffect } from 'react';
import {
  Box,
  Button,
  Container,
  FormControl,
  FormLabel,
  Input,
  Select,
  Stack,
  Textarea,
  useToast,
  VStack,
  Text,
  Heading,
  Spinner,
  Alert,
  AlertIcon,
  AlertTitle,
  AlertDescription,
} from '@chakra-ui/react';
import { useParams, useNavigate } from 'react-router-dom';
import { supabase } from '../utils/supabase';
import { useAuth } from '../contexts/AuthContext';

const FormView: React.FC = () => {
  const { formId, shortUrl } = useParams<{ formId: string; shortUrl: string }>();
  const [form, setForm] = useState<any>(null);
  const [formData, setFormData] = useState<Record<string, any>>({});
  const [isLoading, setIsLoading] = useState(true);
  const [isSubmitting, setIsSubmitting] = useState(false);
  const { user } = useAuth();
  const toast = useToast();
  const navigate = useNavigate();

  useEffect(() => {
    fetchForm();
  }, [formId, shortUrl]);

  const fetchForm = async () => {
    try {
      let query = supabase.from('forms').select('*');

      if (shortUrl) {
        query = query.eq('short_url', shortUrl);
      } else {
        query = query.eq('id', formId);
      }

      const { data: form, error } = await query.single();

      if (error) throw error;

      // If form is a draft, only allow access to the creator
      if (form.status === 'draft') {
        if (!user || user.id !== form.user_id) {
          toast({
            title: 'Access Denied',
            description: 'This form is not published yet',
            status: 'error',
            duration: 5000,
            isClosable: true,
          });
          navigate('/dashboard');
          return;
        }
      }

      setForm(form);
    } catch (error: any) {
      toast({
        title: 'Error',
        description: error.message || 'Failed to fetch form',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      // Save submission to database
      const { error: submissionError } = await supabase
        .from('submissions')
        .insert([
          {
            form_id: form.id,
            data: formData,
            whatsapp_number: form.whatsapp_number,
          },
        ]);

      if (submissionError) throw submissionError;

      // Create WhatsApp message
      const message = Object.entries(formData)
        .map(([key, value]) => {
          const field = form.fields.find((f: any) => f.id === key);
          return `${field.label}: ${value}`;
        })
        .join('\n');

      // Construct WhatsApp URL
      const whatsappUrl = `https://wa.me/${form.country_code}${form.whatsapp_number}?text=${encodeURIComponent(message)}`;

      // Open WhatsApp in new tab
      window.open(whatsappUrl, '_blank');

      toast({
        title: 'Success',
        description: 'Form submitted successfully',
        status: 'success',
        duration: 3000,
        isClosable: true,
      });

      // Reset form
      setFormData({});
    } catch (error: any) {
      toast({
        title: 'Error',
        description: error.message || 'Failed to submit form',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  if (isLoading) {
    return (
      <Container maxW="container.md" py={8}>
        <Box textAlign="center">
          <Spinner size="xl" />
          <Text mt={4}>Loading form...</Text>
        </Box>
      </Container>
    );
  }

  if (!form) {
    return (
      <Container maxW="container.md" py={8}>
        <Alert status="error">
          <AlertIcon />
          <AlertTitle>Form not found!</AlertTitle>
          <AlertDescription>
            The form you're looking for doesn't exist or has been removed.
          </AlertDescription>
        </Alert>
      </Container>
    );
  }

  return (
    <Container maxW="container.md" py={8}>
      <VStack spacing={8} align="stretch">
        <Box>
          <Heading size="lg" mb={2}>
            {form.title}
          </Heading>
          {form.description && (
            <Text color="gray.600" mb={4}>
              {form.description}
            </Text>
          )}
        </Box>

        <form onSubmit={handleSubmit}>
          <VStack spacing={6}>
            {form.fields.map((field: any) => (
              <FormControl
                key={field.id}
                isRequired={field.required}
              >
                <FormLabel>{field.label}</FormLabel>
                {field.type === 'text' && (
                  <Input
                    value={formData[field.id] || ''}
                    onChange={(e) =>
                      setFormData({ ...formData, [field.id]: e.target.value })
                    }
                    placeholder={field.placeholder || `Enter ${field.label.toLowerCase()}`}
                  />
                )}
                {field.type === 'email' && (
                  <Input
                    type="email"
                    value={formData[field.id] || ''}
                    onChange={(e) =>
                      setFormData({ ...formData, [field.id]: e.target.value })
                    }
                    placeholder={field.placeholder || 'Enter your email'}
                  />
                )}
                {field.type === 'select' && (
                  <Select
                    value={formData[field.id] || ''}
                    onChange={(e) =>
                      setFormData({ ...formData, [field.id]: e.target.value })
                    }
                    placeholder="Select an option"
                  >
                    {field.options?.map((option: string) => (
                      <option key={option} value={option}>
                        {option}
                      </option>
                    ))}
                  </Select>
                )}
              </FormControl>
            ))}

            <Button
              type="submit"
              colorScheme="blue"
              size="lg"
              width="full"
              isLoading={isSubmitting}
            >
              Submit
            </Button>
          </VStack>
        </form>
      </VStack>
    </Container>
  );
};

export default FormView;
