import React, { useState, useEffect } from 'react';
import {
  Box,
  Button,
  Container,
  FormControl,
  FormLabel,
  Input,
  Select,
  Stack,
  Textarea,
  useToast,
  VStack,
  HStack,
  IconButton,
  Text,
  Divider,
  useDisclosure,
  Modal,
  ModalOverlay,
  ModalContent,
  ModalHeader,
  ModalBody,
  ModalCloseButton,
  ModalFooter,
  Grid,
  GridItem,
  Heading,
  Switch,
  FormHelperText,
  Badge,
  Card,
  CardBody,
  CardHeader,
  CardFooter,
  Flex,
  Spacer,
  Tooltip,
  useColorModeValue,
  Icon,
} from '@chakra-ui/react';
import { AddIcon, DeleteIcon, DragHandleIcon, ViewIcon, EditIcon, CopyIcon } from '@chakra-ui/icons';
import { useNavigate, useParams } from 'react-router-dom';
import { supabase } from '../utils/supabase';
import { Form, FormField } from '../types';
import { DragDropContext, Droppable, Draggable, DropResult, DroppableProvided, DraggableProvided } from '@hello-pangea/dnd';
import { FaHeading, FaRegKeyboard, FaHashtag, FaCalendarAlt, FaChevronDown, FaDotCircle, FaCheckSquare, FaFileUpload, FaWhatsapp, FaPlus } from 'react-icons/fa';
import { useAuth } from '../contexts/AuthContext';
import Header from '../components/Header';
import BreadcrumbNav from '../components/BreadcrumbNav';

const FormBuilder: React.FC = () => {
  const { formId } = useParams<{ formId: string }>();
  const { user } = useAuth();
  const [form, setForm] = useState<Partial<Form>>({
    title: '',
    description: '',
    fields: [],
    status: 'draft',
  });
  const [isLoading, setIsLoading] = useState(false);
  const [isPreviewMode, setIsPreviewMode] = useState(false);
  const { isOpen, onOpen, onClose } = useDisclosure();
  const [draftTitle, setDraftTitle] = useState('');
  const toast = useToast();
  const navigate = useNavigate();

  // Color scheme from UI screenshot
  const green = '#22C55E';
  const blue = '#38BDF8';
  const bgMain = '#F8FAFC';
  const cardBg = useColorModeValue('white', 'gray.800');
  const borderColor = useColorModeValue('gray.200', 'gray.700');
  const mutedText = '#64748B';
  const headingText = '#0F172A';

  // Elements panel state
  const [elementsTab, setElementsTab] = useState<'basic' | 'advanced' | 'layout'>('basic');
  const [search, setSearch] = useState('');

  // List of available elements with appropriate icon components
  const availableElements = [
    { type: 'text', label: 'Short Text', icon: FaRegKeyboard },
    { type: 'textarea', label: 'Long Text', icon: FaHeading },
    { type: 'number', label: 'Number', icon: FaHashtag },
    { type: 'tel', label: 'Phone', icon: FaRegKeyboard },
    { type: 'email', label: 'Email', icon: FaRegKeyboard },
    { type: 'date', label: 'Date', icon: FaCalendarAlt },
    { type: 'time', label: 'Time', icon: FaRegKeyboard },
    { type: 'select', label: 'Drop Down', icon: FaChevronDown },
    { type: 'radio', label: 'Radio Button', icon: FaDotCircle },
    { type: 'checkbox', label: 'Check Boxes', icon: FaCheckSquare },
    { type: 'file', label: 'File Upload', icon: FaFileUpload },
  ];

  // Filtered elements for search
  const filteredElements = availableElements.filter(e =>
    e.label.toLowerCase().includes(search.toLowerCase())
  );

  // Drag and drop logic for cross-column
  const onDragEnd = (result: DropResult) => {
    const { source, destination, draggableId } = result;
    if (!destination) return;

    // Drag from Elements panel to Form Designer
    if (source.droppableId === 'elements' && destination.droppableId === 'fields') {
      const element = availableElements[source.index];
      const newField: FormField = {
        id: Date.now().toString(),
        type: element.type as FormField['type'],
        label: `New ${element.label}`,
        required: false,
      };
      setForm(prev => {
        const fields = prev.fields ? [...prev.fields] : [];
        fields.splice(destination.index, 0, newField);
        return { ...prev, fields };
      });
      return;
    }

    // Reorder within Form Designer
    if (source.droppableId === 'fields' && destination.droppableId === 'fields') {
      const items = Array.from(form.fields || []);
      const [reorderedItem] = items.splice(source.index, 1);
      items.splice(destination.index, 0, reorderedItem);
      setForm(prev => ({ ...prev, fields: items }));
      return;
    }
  };

  useEffect(() => {
    if (formId) {
      fetchForm();
    }
  }, [formId]);

  const fetchForm = async () => {
    try {
      const { data, error } = await supabase
        .from('forms')
        .select('*')
        .eq('id', formId)
        .single();

      if (error) throw error;
      setForm(data);
    } catch (error: any) {
      toast({
        title: 'Error',
        description: error.message || 'Failed to fetch form',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    }
  };

  const handleSave = async (isDraft: boolean = true) => {
    try {
      setIsLoading(true);
      // Only include fields that exist in the forms table schema
      const formData = {
        title: form.title,
        description: form.description,
        fields: form.fields,
        status: isDraft ? 'draft' : 'published',
        user_id: user?.id,
        whatsapp_number: form.whatsapp_number,
        country_code: form.country_code,
      };

      if (formId) {
        const { error } = await supabase
          .from('forms')
          .update(formData)
          .eq('id', formId);

        if (error) throw error;
      } else {
        const { data, error } = await supabase
          .from('forms')
          .insert([formData])
          .select()
          .single();

        if (error) throw error;
        navigate(`/forms/${data.id}/edit`);
      }

      toast({
        title: 'Success',
        description: `Form ${isDraft ? 'saved as draft' : 'published'} successfully`,
        status: 'success',
        duration: 3000,
        isClosable: true,
      });

      if (!isDraft) {
        navigate('/dashboard');
      }
    } catch (error: any) {
      toast({
        title: 'Error',
        description: error.message || 'Failed to save form',
        status: 'error',
        duration: 5000,
        isClosable: true,
      });
    } finally {
      setIsLoading(false);
    }
  };

  const addField = (type: FormField['type']) => {
    const newField: FormField = {
      id: Date.now().toString(),
      type,
      label: `New ${type} field`,
      required: false,
    };
    setForm(prev => ({
      ...prev,
      fields: [...(prev.fields || []), newField],
    }));
  };

  const updateField = (index: number, field: Partial<FormField>) => {
    setForm(prev => ({
      ...prev,
      fields: prev.fields?.map((f, i) => 
        i === index ? { ...f, ...field } : f
      ),
    }));
  };

  const removeField = (index: number) => {
    setForm(prev => ({
      ...prev,
      fields: prev.fields?.filter((_, i) => i !== index),
    }));
  };

  const renderFieldEditor = (field: FormField, index: number, provided: DraggableProvided) => (
    <Box
      ref={provided.innerRef}
      {...provided.draggableProps}
    >
      <Card 
        key={field.id} 
        mb={4} 
        variant="outline" 
        bg={cardBg}
        borderColor={borderColor}
        boxShadow="sm"
        _hover={{ boxShadow: 'md' }}
        transition="all 0.2s"
      >
        <CardHeader pb={0}>
          <Flex align="center">
            <Box {...provided.dragHandleProps} cursor="grab">
              <DragHandleIcon mr={2} color={mutedText} />
            </Box>
            <Heading size="sm" color={headingText}>Field {index + 1}</Heading>
            <Spacer />
            <Tooltip label="Delete field">
              <IconButton
                aria-label="Delete field"
                icon={<DeleteIcon />}
                size="sm"
                colorScheme="red"
                variant="ghost"
                onClick={() => removeField(index)}
              />
            </Tooltip>
          </Flex>
        </CardHeader>
        <CardBody>
          <VStack spacing={4}>
            <FormControl>
              <FormLabel fontWeight="medium" color={headingText}>Label {field.required && <span style={{ color: 'red' }}>*</span>}</FormLabel>
              <Input
                value={field.label}
                onChange={(e) => updateField(index, { label: e.target.value })}
                placeholder="Enter field label"
                size="md"
                borderRadius="md"
                bg="white"
                borderColor={borderColor}
              />
            </FormControl>

            {/* Validation controls by type */}
            {['text', 'textarea'].includes(field.type) && (
              <HStack w="100%">
                <FormControl>
                  <FormLabel fontWeight="medium" color={headingText}>Min Length</FormLabel>
                  <Input
                    type="number"
                    value={field.validation?.min || ''}
                    onChange={e => updateField(index, { validation: { ...field.validation, min: e.target.value ? Number(e.target.value) : undefined } })}
                    placeholder="Min"
                    size="sm"
                    borderRadius="md"
                    bg="white"
                    borderColor={borderColor}
                  />
                </FormControl>
                <FormControl>
                  <FormLabel fontWeight="medium" color={headingText}>Max Length</FormLabel>
                  <Input
                    type="number"
                    value={field.validation?.max || ''}
                    onChange={e => updateField(index, { validation: { ...field.validation, max: e.target.value ? Number(e.target.value) : undefined } })}
                    placeholder="Max"
                    size="sm"
                    borderRadius="md"
                    bg="white"
                    borderColor={borderColor}
                  />
                </FormControl>
                <FormControl>
                  <FormLabel fontWeight="medium" color={headingText}>Pattern</FormLabel>
                  <Input
                    value={field.validation?.pattern || ''}
                    onChange={e => updateField(index, { validation: { ...field.validation, pattern: e.target.value } })}
                    placeholder="Regex pattern"
                    size="sm"
                    borderRadius="md"
                    bg="white"
                    borderColor={borderColor}
                  />
                </FormControl>
              </HStack>
            )}
            {field.type === 'number' && (
              <HStack w="100%">
                <FormControl>
                  <FormLabel fontWeight="medium" color={headingText}>Min</FormLabel>
                  <Input
                    type="number"
                    value={field.validation?.min || ''}
                    onChange={e => updateField(index, { validation: { ...field.validation, min: e.target.value ? Number(e.target.value) : undefined } })}
                    placeholder="Min"
                    size="sm"
                    borderRadius="md"
                    bg="white"
                    borderColor={borderColor}
                  />
                </FormControl>
                <FormControl>
                  <FormLabel fontWeight="medium" color={headingText}>Max</FormLabel>
                  <Input
                    type="number"
                    value={field.validation?.max || ''}
                    onChange={e => updateField(index, { validation: { ...field.validation, max: e.target.value ? Number(e.target.value) : undefined } })}
                    placeholder="Max"
                    size="sm"
                    borderRadius="md"
                    bg="white"
                    borderColor={borderColor}
                  />
                </FormControl>
              </HStack>
            )}
            {['email', 'tel'].includes(field.type) && (
              <HStack w="100%">
                <FormControl>
                  <FormLabel fontWeight="medium" color={headingText}>Max Length</FormLabel>
                  <Input
                    type="number"
                    value={field.validation?.max || ''}
                    onChange={e => updateField(index, { validation: { ...field.validation, max: e.target.value ? Number(e.target.value) : undefined } })}
                    placeholder="Max"
                    size="sm"
                    borderRadius="md"
                    bg="white"
                    borderColor={borderColor}
                  />
                </FormControl>
              </HStack>
            )}

            {(field.type === 'select' || field.type === 'radio' || field.type === 'checkbox') && (
              <FormControl>
                <FormLabel fontWeight="medium" color={headingText}>Options</FormLabel>
                <VStack align="stretch" spacing={2}>
                  {(field.options || []).map((option, optIdx) => (
                    <HStack key={optIdx}>
                      <Input
                        value={option}
                        onChange={e => {
                          const newOptions = [...(field.options || [])];
                          newOptions[optIdx] = e.target.value;
                          updateField(index, { options: newOptions });
                        }}
                        placeholder={`Option ${optIdx + 1}`}
                        size="sm"
                        borderRadius="md"
                        bg="white"
                        borderColor={borderColor}
                      />
                      <IconButton
                        aria-label="Remove option"
                        icon={<DeleteIcon />}
                        size="xs"
                        colorScheme="red"
                        variant="ghost"
                        onClick={() => {
                          const newOptions = (field.options || []).filter((_, i) => i !== optIdx);
                          updateField(index, { options: newOptions });
                        }}
                      />
                    </HStack>
                  ))}
                  <Button
                    size="xs"
                    leftIcon={<AddIcon />}
                    onClick={() => updateField(index, { options: [...(field.options || []), ''] })}
                  >
                    Add Option
                  </Button>
                </VStack>
              </FormControl>
            )}

            <FormControl>
              <FormLabel fontWeight="medium" color={headingText}>Placeholder</FormLabel>
              <Input
                value={field.placeholder || ''}
                onChange={(e) => updateField(index, { placeholder: e.target.value })}
                placeholder="Enter placeholder text"
                size="md"
                borderRadius="md"
                bg="white"
                borderColor={borderColor}
              />
            </FormControl>

            <FormControl display="flex" alignItems="center">
              <FormLabel mb="0" fontWeight="medium" color={headingText}>Required</FormLabel>
              <Switch
                isChecked={field.required}
                onChange={(e) => updateField(index, { required: e.target.checked })}
                colorScheme="blue"
              />
            </FormControl>

            {field.type === 'file' && (
              <FormControl>
                <FormLabel fontWeight="medium" color={headingText}>Allowed File Types</FormLabel>
                <Input
                  value={field.validation?.allowedTypes || ''}
                  onChange={e => updateField(index, { validation: { ...field.validation, allowedTypes: e.target.value } })}
                  placeholder="e.g. .pdf,.jpg,.png"
                  size="sm"
                  borderRadius="md"
                  bg="white"
                  borderColor={borderColor}
                />
                <FormHelperText color={mutedText} fontSize="xs">
                  Comma-separated (e.g. .pdf,.jpg,.png)
                </FormHelperText>
              </FormControl>
            )}
          </VStack>
        </CardBody>
      </Card>
    </Box>
  );

  const renderFieldPreview = (field: FormField) => (
    <Card 
      key={field.id} 
      mb={4} 
      variant="outline" 
      bg={cardBg}
      borderColor={borderColor}
      boxShadow="sm"
    >
      <CardBody>
        <VStack spacing={4} align="stretch">
          <HStack justify="space-between">
            <FormLabel fontWeight="medium" mb={0} color={headingText}>
              {field.label} {field.required && <span style={{ color: 'red' }}>*</span>}
            </FormLabel>
          </HStack>
          {renderFieldInput(field, true)}
        </VStack>
      </CardBody>
    </Card>
  );

  const FileInputPreview: React.FC<{ field: FormField }> = ({ field }) => {
    const [fileError, setFileError] = useState<string | null>(null);
    return (
      <Box>
        <Input
          type="file"
          accept={field.validation?.allowedTypes || undefined}
          onChange={e => {
            const file = e.target.files?.[0];
            setFileError(null);
            if (file) {
              if (file.size > 10 * 1024 * 1024) {
                setFileError('File size must be 10MB or less.');
              } else if (
                field.validation?.allowedTypes &&
                !field.validation.allowedTypes
                  .split(',')
                  .map((t: string) => t.trim())
                  .some((type: string) => file.name.endsWith(type))
              ) {
                setFileError('Invalid file type.');
              }
            }
          }}
        />
        {fileError && <Text color="red.500" fontSize="sm" mt={1}>{fileError}</Text>}
      </Box>
    );
  };

  const renderFieldInput = (field: FormField, previewMode = false) => {
    switch (field.type) {
      case 'text':
      case 'email':
      case 'tel':
      case 'number':
        return (
          <Input
            type={field.type}
            placeholder={field.placeholder}
            size="md"
            borderRadius="md"
            bg="white"
            borderColor={borderColor}
          />
        );
      case 'textarea':
        return (
          <Input
            as="textarea"
            placeholder={field.placeholder}
            size="md"
            rows={3}
            borderRadius="md"
            bg="white"
            borderColor={borderColor}
          />
        );
      case 'select':
        return (
          <Select placeholder={field.placeholder} size="md" borderRadius="md" bg="white" borderColor={borderColor}>
            {field.options?.map((option, i) => (
              <option key={i} value={option}>{option}</option>
            ))}
          </Select>
        );
      case 'radio':
        return (
          <VStack align="start" spacing={2}>
            {field.options?.map((option, i) => (
              <HStack key={i}>
                <input type="radio" name={field.id} value={option} />
                <Text>{option}</Text>
              </HStack>
            ))}
          </VStack>
        );
      case 'checkbox':
        return (
          <VStack align="start" spacing={2}>
            {field.options?.map((option, i) => (
              <HStack key={i}>
                <input type="checkbox" value={option} />
                <Text>{option}</Text>
              </HStack>
            ))}
          </VStack>
        );
      case 'date':
        return (
          <Input type="date" size="md" borderRadius="md" bg="white" borderColor={borderColor} />
        );
      case 'time':
        return (
          <Input type="time" size="md" borderRadius="md" bg="white" borderColor={borderColor} />
        );
      case 'file':
        return <FileInputPreview field={field} />;
      default:
        return null;
    }
  };

  return (
    <>
      <Header />
      <BreadcrumbNav />
      <Box bg="background.tertiary" minH="100vh" py={8}>
        <Container maxW="container.xl">
          <Grid
            templateColumns={{ base: '1fr', lg: '1.1fr 2fr 1.2fr' }}
            gap={8}
            alignItems="flex-start"
          >
            {/* Elements Panel */}
            <GridItem>
              <Box
                bg="white"
                borderRadius="card"
                boxShadow="card"
                border="1px solid"
                borderColor="gray.100"
                minH="600px"
                p={0}
              >
                <Box borderBottom="1px solid" borderColor={borderColor} pb={3} pt={4} px={5} borderTopRadius="2xl">
                  <Heading size="md" color={headingText} fontWeight="bold">Elements</Heading>
                  <Text fontSize="sm" color={mutedText} mt={1}>Drag and drop or click to add</Text>
                  <Input mt={4} size="sm" placeholder="Search elements..." value={search} onChange={e => setSearch(e.target.value)} borderRadius="md" bg="white" borderColor={borderColor} />
                </Box>
                <Box px={2} py={3}>
                  <Droppable droppableId="elements" isDropDisabled={true}>
                    {(provided) => (
                      <VStack align="stretch" spacing={1} divider={<Divider borderColor={borderColor} />} ref={provided.innerRef} {...provided.droppableProps}>
                        {filteredElements.map((el, idx) => (
                          <Draggable key={el.type + idx} draggableId={`element-${el.type}-${idx}`} index={idx}>
                            {(dragProvided) => (
                              <Button
                                ref={dragProvided.innerRef}
                                {...dragProvided.draggableProps}
                                {...dragProvided.dragHandleProps}
                                leftIcon={<Icon as={el.icon as React.ElementType} boxSize={5} />}
                                variant="ghost"
                                justifyContent="flex-start"
                                size="md"
                                fontWeight="medium"
                                color={headingText}
                                borderRadius="md"
                                px={3}
                                _hover={{ bg: '#E5E7EB', color: green }}
                                transition="all 0.15s"
                                onClick={() => {
                                  const newField = {
                                    id: Date.now().toString(),
                                    type: el.type as FormField['type'],
                                    label: `New ${el.label}`,
                                    required: false,
                                  };
                                  setForm(prev => ({
                                    ...prev,
                                    fields: [...(prev.fields || []), newField],
                                  }));
                                }}
                              >
                                {el.label}
                              </Button>
                            )}
                          </Draggable>
                        ))}
                        {provided.placeholder}
                      </VStack>
                    )}
                  </Droppable>
                </Box>
              </Box>
            </GridItem>

            {/* Form Designer */}
            <GridItem>
              <Box
                bg="white"
                borderRadius="card"
                boxShadow="card"
                border="1px solid"
                borderColor="gray.100"
                minH="600px"
                p={0}
              >
                <Box pb={0} pt={4} px={5} borderTopRadius="2xl">
                  <VStack align="stretch" spacing={4}>
                    <FormControl>
                      <FormLabel fontWeight="medium" color={headingText}>Form Title</FormLabel>
                      <Input
                        value={form.title}
                        onChange={(e) => setForm(prev => ({ ...prev, title: e.target.value }))}
                        placeholder="Enter form title"
                        size="lg"
                        borderRadius="md"
                        bg="white"
                        borderColor={borderColor}
                      />
                    </FormControl>
                    <FormControl>
                      <FormLabel fontWeight="medium" color={headingText}>Description</FormLabel>
                      <Input
                        value={form.description}
                        onChange={(e) => setForm(prev => ({ ...prev, description: e.target.value }))}
                        placeholder="Enter form description"
                        size="md"
                        borderRadius="md"
                        bg="white"
                        borderColor={borderColor}
                      />
                    </FormControl>
                  </VStack>
                </Box>
                <Box px={5} py={6}>
                  {!isPreviewMode ? (
                    <Droppable droppableId="fields">
                      {(provided: DroppableProvided, snapshot) => (
                        <Box
                          {...provided.droppableProps}
                          ref={provided.innerRef}
                          minH="200px"
                          p={2}
                          bg="#F8FAFC"
                          textAlign={form.fields?.length ? 'left' : 'center'}
                          display="flex"
                          flexDirection="column"
                          alignItems="center"
                          justifyContent={form.fields?.length ? 'flex-start' : 'center'}
                        >
                          {form.fields?.length === 0 && !snapshot.isDraggingOver && (
                            <Box
                              w="100%"
                              py={8}
                              display="flex"
                              flexDirection="column"
                              alignItems="center"
                              border="2px dashed"
                              borderColor={green}
                              borderRadius="xl"
                              bg="#F8FAFC"
                            >
                              <VStack color={green} spacing={2}>
                                <Icon as={FaPlus as React.ElementType} boxSize={7} />
                                <Text color={mutedText} fontWeight="medium">Drag and drop elements here</Text>
                              </VStack>
                            </Box>
                          )}
                          {form.fields?.map((field, index) => (
                            <Box key={field.id} w="100%" mb={2}>
                              <Draggable draggableId={field.id} index={index}>
                                {(provided: DraggableProvided) => (
                                  <Box ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps} w="100%">
                                    <Box bg={cardBg} borderRadius="xl" borderWidth={1} borderColor={borderColor} boxShadow="sm" p={4}>
                                      {renderFieldEditor(field, index, provided)}
                                    </Box>
                                  </Box>
                                )}
                              </Draggable>
                            </Box>
                          ))}
                          {provided.placeholder}
                        </Box>
                      )}
                    </Droppable>
                  ) : (
                    <VStack spacing={2} align="stretch">
                      <Box>
                        <Heading size="lg" color={headingText} fontWeight="bold">{form.title || 'Untitled Form'}</Heading>
                        {form.description && (
                          <Text color={mutedText} mt={2}>{form.description}</Text>
                        )}
                      </Box>
                      <Divider />
                      {form.fields?.map((field) => (
                        <Box key={field.id} w="100%" mb={2}>
                          <Box bg={cardBg} borderRadius="xl" borderWidth={1} borderColor={borderColor} boxShadow="sm" p={4}>
                            {renderFieldPreview(field)}
                          </Box>
                        </Box>
                      ))}
                      <Button leftIcon={<Icon as={FaWhatsapp as React.ElementType} boxSize={6} />} bg={green} color="white" size="lg" fontWeight="bold" borderRadius="lg" px={8} py={6} fontSize="lg" _hover={{ bg: '#16A34A' }}>
                        Submit via WhatsApp
                      </Button>
                    </VStack>
                  )}
                </Box>
              </Box>
            </GridItem>

            {/* Form Preview */}
            <GridItem>
              <Box
                bg="white"
                borderRadius="card"
                boxShadow="card"
                border="1px solid"
                borderColor="gray.100"
                minH="600px"
                p={0}
              >
                <Box pb={2} pt={4} px={5} borderTopRadius="2xl">
                  <Heading size="md" color={headingText} fontWeight="bold">Form Preview</Heading>
                  <Text fontSize="sm" color={mutedText} mt={1}>This is how your form will appear to users</Text>
                </Box>
                <Box px={5} py={6}>
                  <VStack spacing={6} align="stretch">
                    <Box>
                      <Heading size="lg" color={headingText} fontWeight="bold">{form.title || 'Untitled Form'}</Heading>
                      {form.description && (
                        <Text color={mutedText} mt={2}>{form.description}</Text>
                      )}
                    </Box>
                    <Divider />
                    {form.fields?.map((field) => renderFieldPreview(field))}
                    <Button leftIcon={<Icon as={FaWhatsapp as React.ElementType} boxSize={6} />} bg={green} color="white" size="lg" fontWeight="bold" borderRadius="lg" px={8} py={6} fontSize="lg" _hover={{ bg: '#16A34A' }}>
                      Submit via WhatsApp
                    </Button>
                  </VStack>
                </Box>
              </Box>
            </GridItem>
          </Grid>
        </Container>
      </Box>
    </>
  );
};

export default FormBuilder;
